   const defaultStore = {
           default: DefalutElementComponent,
           text: TextComponent,
           leaf: LeafComponent,
           placeholder: PlaceholderComponent
   }

   // 新建一个Node对应的Component
   @Component({...})
   export class ComponentA {
           static type = 'A' // 组件的标识
   }

   // 使用service进行注册
   this.registryService.add([ComponentA, ComponentB])

   // 最终得到的store
   const store = {
           ...defaultStore,
           A: ComponentA
   }

   // 获取时直接从对象根据type获取
```
